package week1.day2;

public class NegToPos {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = -40;
		if (num < 0) {
		   	int pos = num * -1;
		   	System.out.println("Negative number "+num+" is converted to positive number: "+pos );
			
			}
			
		}
		
		
	}


